namespace QuickGraph.Graphviz.Dot
{
    using System;
    using System.ComponentModel;

    public enum GraphvizImageType
    {
        /// <summary>
        /// Client-side imagemaps
        /// </summary>
        Cmap = 6,
        Fig = 0,
        Gd = 1,
        Gd2 = 2,
        Gif = 3,
        /// <summary>
        /// HP-GL/2 format
        /// </summary>
        Hpgl = 4,
        /// <summary>
