        /// </summary>
        Imap = 5,
        Jpeg = 7,
        /// <summary>
        /// FrameMaker MIF format
        /// </summary>
        Mif = 8,
        /// <summary>
        /// MetaPost
        /// </summary>
        Mp = 9,
        /// <summary>
        /// PCL format
        /// </summary>
        Pcl = 10,
        /// <summary>
        /// PIC format
        /// </summary>
        Pic = 11,
        /// <summary>
        /// plain text format
        /// </summary>
        PlainText = 12,
        /// <summary>
